import React, {Component} from 'react';
import PropTypes from 'prop-types';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import UsuarioService from "../../../services/Usuario/UsuarioService";
import SexoService from "../../../services/Sexo/SexoService";
import EstadosService from "../../../services/Estados/EstadosService";
import TipoPermisoService from "../../../services/TipoPermiso/TipoPermisoService";

class ModalCargo extends Component {

    state = {};

    constructor() {
        super();
        this.state = {
            open: false,

            monto_cargo: 750.00,
            id_usuario: '',
            id_cat_sexo: '',
            id_cat_estado: '',
            id_cat_tipo_permiso: '',
            username: '',
            password: '',
            nombre: '',
            apellido_paterno: '',
            apellido_materno: '',
            saldo: '',
            email: '',
            telefono: '',
            direccion: '',
            foto_base64: '',
            foto_base64Tipo: '',
            foto_archivo: '',
            foto_formato: '',

            cat_sexo: [],
            cat_estado: [],
            cat_tipo_permiso: [],

        };
    }

    open = () => {
        let item = this.props.item;
        this.setState({
            open: true,

            id_usuario: item.id_usuario || '',
            id_cat_sexo: item.id_cat_sexo || '',
            id_cat_estado: item.id_cat_estado || '',
            id_cat_tipo_permiso: item.id_cat_tipo_permiso || '',
            saldo: item.saldo || '',
            email: item.email || '',
            telefono: item.telefono || '',
            direccion: item.direccion || '',
            username: item.username || '',
            password: item.password || '',
            nombre: item.nombre || '',
            apellido_paterno: item.apellido_paterno || '',
            apellido_materno: item.apellido_materno || '',

            foto_base64: '',
            foto_base64Tipo: '',
            foto_archivo: '',
            foto_formato: '',
        });
        if (item.id_cat_sexo > 0) {
            this.show(item);
        }
        SexoService.get().then(response => {
            this.setState({
                cat_sexo: response.data
            })
        }).catch(error => {
            alert(error.mensaje);
        })
        EstadosService.get().then(response => {
            this.setState({
                cat_estado: response.data
            })
        }).catch(error => {
            alert(error.mensaje);
        })
        TipoPermisoService.get().then(response => {
            this.setState({
                cat_tipo_permiso: response.data
            })
        }).catch(error => {
            alert(error.mensaje);
        })
    };

    close = () => {
        this.setState({
            open: false,

            id_usuario: '',
            id_cat_sexo: '',
            id_cat_estado: '',
            username: '',
            password: '',
            nombre: '',
            saldo: '',
            tipo_permiso: '',
            email: '',
            telefono: '',
            direccion: '',
            apellido_paterno: '',
            apellido_materno: '',

            foto_base64: '',
            foto_base64Tipo: '',
            foto_archivo: '',
            foto_formato: '',
        });
    };


    show = (item) => {
        UsuarioService.show(item).then(response => {
            this.setState({
                id_usuario: response.data.id_usuario || '',
                id_cat_sexo: response.data.id_cat_sexo || '',
                id_cat_estado: response.data.id_cat_estado || '',
                id_cat_tipo_permiso: response.data.id_cat_tipo_permiso || '',
                username: response.data.username || '',
                password: response.data.password || '',
                nombre: response.data.nombre || '',
                apellido_paterno: response.data.apellido_paterno || '',
                apellido_materno: response.data.apellido_materno || '',
                saldo: response.data.saldo || '',
                email: response.data.email || '',
                telefono: response.telefono,
                direccion: response.direccion,
                foto_base64: '',
                foto_base64Tipo: '',
                foto_archivo: response.data.foto_archivo || '',
                foto_formato: response.data.foto_formato || '',
            });
        }).catch(error => {
            alert(error.mensaje);
        });
    };

    save = () => {
        if (this.state.id_usuario > 0) {
            this.update();
        } else {
            this.create();
        }
    };

    create = () => {
        UsuarioService.create(this.state).then(response => {
            alert(response.mensaje);
            this.props.RefrechList();
            this.close();
        }).catch(error => {
            alert(error.mensaje);
        });
    };

    update = () => {
        UsuarioService.update(this.state).then(response => {
            alert(response.mensaje);
            this.props.RefrechList();
            this.close();
        }).catch(error => {
            alert(error.mensaje);
        });
    };




    render() {
        return (
            <div>

				<span onClick={this.open}>
					{this.props.componente}
				</span>

                <Dialog
                    open={this.state.open}
                    onClose={() => this.close()}
                    aria-labelledby="alert-dialog-title"
                    aria-describedby="alert-dialog-description"
                    maxWidth="sm"
                >

                    <DialogTitle>
                        Cargo
                    </DialogTitle>

                    <DialogContent>
                        <Grid container spacing={2}>

                            <Grid item xs={6} sm={6} md={6} lg={4} xl={4}>
                                <label> Seguro que desea realizar el cargo en el mes de por la cantidad de ${this.state.monto_cargo}</label>
                            </Grid>

                            <Grid item xs={6} sm={6} md={6} lg={4} xl={4}>
                                <TextField
                                    label="Saldo"
                                    type="text"
                                    margin="normal"
                                    variant="outlined"
                                    fullWidth
                                    value={this.state.saldo}
                                    onChange={(e) => {
                                        this.setState({
                                            saldo: e.target.value
                                        });
                                    }}
                                    disabled={this.props.tipo === 'view'}
                                />
                            </Grid>

                        </Grid>
                    </DialogContent>

                    <DialogActions>
                        <Button variant="contained" onClick={() => this.close()}>
                            Cerrar
                        </Button>
                        <Button variant="contained" color="primary" onClick={() => this.save()} autoFocus>
                            Crear
                        </Button>
                    </DialogActions>

                </Dialog>
            </div>
        );
    };
}

ModalCargo.propTypes = {
    id: PropTypes.oneOfType([
        PropTypes.number.isRequired,
        PropTypes.oneOf([null]).isRequired,
    ]),
    tipo: PropTypes.string.isRequired,
    item: PropTypes.object.isRequired,
    componente: PropTypes.element.isRequired
};

export default ModalCargo;
