import React, {Component, Fragment} from 'react';

import Header from "../../includes/Header";
import UsuarioService from "../../services/Usuario/UsuarioService";
import {KeyboardDatePicker, MuiPickersUtilsProvider} from "@material-ui/pickers";
import DateFnsUtils from "@date-io/date-fns";

import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import TextField from "../Login/includes/ModalRegistro";

class Home extends Component {

    state = {};

    constructor(props) {
        super(props);
        this.state = {
            params: props.match.params,
        };
    }

    RefrechList = () => {
        this.all();
    };

    all = () => {
        UsuarioService.allEstandar().then(response => {
            this.setState({
                lista: response.data
            });
        }).catch(error => {
            this.setState({
                lista: []
            });
            alert(error.mensaje);
        });
    };


    updateDate = (date) => {
        this.setState({
            mes: date.toString()
        })
    }


    render() {

        const {params} = this.props.match;

        const hoy = new Date();

        {
            JSON.stringify(this.state.params, null, 2)
        }

        {
            JSON.stringify(this.state.params, null, 2)
        }

        return (
            <Fragment>

                <Header {...this.props}/>

                <Grid container direction="row" justify="center" alignItems="center" spacing={2}>

                    <Grid item xs={6} sm={6} md={6} lg={4} xl={4}>
                        <TextField
                            label="Usuario"
                            type="text"
                            margin="normal"
                            variant="outlined"
                            fullWidth
                            value={this.state.username}
                            onChange={(e) => {
                                this.setState({
                                    username: e.target.value
                                });
                            }}
                            disabled={this.props.tipo === 'view'}
                        />
                    </Grid>

                    <Grid item xs={6} sm={6} md={6} lg={4} xl={4}>
                        <TextField
                            label="Contraseña"
                            type="text"
                            margin="normal"
                            variant="outlined"
                            fullWidth
                            value={this.state.password}
                            onChange={(e) => {
                                this.setState({
                                    password: e.target.value
                                });
                            }}
                            disabled={this.props.tipo === 'view'}
                        />
                    </Grid>

                    <Grid item xs={6} sm={6} md={6} lg={4} xl={4}>
                        <TextField
                            label="Nombre(s)"
                            type="text"
                            margin="normal"
                            variant="outlined"
                            fullWidth
                            value={this.state.nombre}
                            onChange={(e) => {
                                this.setState({
                                    nombre: e.target.value
                                });
                            }}
                            disabled={this.props.tipo === 'view'}
                        />
                    </Grid>

                    <Grid item xs={6} sm={6} md={6} lg={4} xl={4}>
                        <TextField
                            label="Apellido paterno"
                            type="text"
                            margin="normal"
                            variant="outlined"
                            fullWidth
                            value={this.state.apellido_paterno}
                            onChange={(e) => {
                                this.setState({
                                    apellido_paterno: e.target.value
                                });
                            }}
                            disabled={this.props.tipo === 'view'}
                        />
                    </Grid>

                    <Grid item xs={6} sm={6} md={6} lg={4} xl={4}>
                        <TextField
                            label="Apellido materno"
                            type="text"
                            margin="normal"
                            variant="outlined"
                            fullWidth
                            value={this.state.apellido_materno}
                            onChange={(e) => {
                                this.setState({
                                    apellido_materno: e.target.value
                                });
                            }}
                            disabled={this.props.tipo === 'view'}
                        />
                    </Grid>

                    <Grid item xs={6} sm={6} md={6} lg={4} xl={4}>
                        <TextField
                            label="Teléfono"
                            type="numeric"
                            margin="normal"
                            variant="outlined"
                            fullWidth
                            value={this.state.telefono}
                            onChange={(e) => {
                                this.setState({
                                    telefono: e.target.value
                                });
                            }}
                            disabled={this.props.tipo === 'view'}
                        />
                    </Grid>

                    <Grid item xs={6} sm={6} md={6} lg={4} xl={4}>
                        <TextField
                            label="Email"
                            type="text"
                            margin="normal"
                            variant="outlined"
                            fullWidth
                            value={this.state.email}
                            onChange={(e) => {
                                this.setState({
                                    email: e.target.value
                                });
                            }}
                            disabled={this.props.tipo === 'view'}
                        />
                    </Grid>

                    <Grid item xs={12} sm={12} md={12} lg={12} xl={12}>
                        <TextField
                            label="Dirección"
                            type="text"
                            margin="normal"
                            variant="outlined"
                            fullWidth
                            value={this.state.direccion}
                            onChange={(e) => {
                                this.setState({
                                    direccion: e.target.value
                                });
                            }}
                            disabled={this.props.tipo === 'view'}
                        />
                    </Grid>

                    <Grid item xs={6} sm={6} md={6} lg={4} xl={4}>
                        <TextField
                            label="Saldo"
                            type="text"
                            margin="normal"
                            variant="outlined"
                            fullWidth
                            value={this.state.saldo}
                            onChange={(e) => {
                                this.setState({
                                    saldo: e.target.value
                                });
                            }}
                            disabled={this.props.tipo === 'view'}
                        />
                    </Grid>
                </Grid>

            </Fragment>
        );
    }
}

export default Home;
