import React, {Component, Fragment} from "react";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import {HomeOutlined, ListAltOutlined, MenuOutlined, PersonOutlined} from "@material-ui/icons";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import $State, {ReactLocalStorageService} from '../settings/Libs/Libs';
import Drawer from "@material-ui/core/Drawer";
import Hidden from '@material-ui/core/Hidden';
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import { withStyles} from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Divider from '@material-ui/core/Divider';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';

const drawerWidth = 240;

const styles = theme => ({
    root: {
        display: 'flex',
    },
    appBar: {
        width: `calc(100% - ${drawerWidth}px)`,
        marginLeft: drawerWidth,
    },
    drawer: {
        width: drawerWidth,
        flexShrink: 0,
    },
    drawerPaper: {
        width: drawerWidth,
    },
    toolbar: theme.mixins.toolbar,
    content: {
        flexGrow: 1,
        backgroundColor: theme.palette.background.default,
        padding: theme.spacing(3),
    },
});


class Header extends Component {

    state = {};

    constructor(props) {
        super(props);
        this.state = {
            menu: false
        };
    }

    openMenu = () => {
        this.setState({
            menu: true
        })
    };

    closeMenu = () => {
        this.setState({
            menu: false
        })
    };

    Login = () => {
        $State.go(this.props, 'login', {});
    };

    Home = () => {
        $State.go(this.props, 'home', {});
    };

    Administrador = () => {
        $State.go(this.props, 'administrador', {});
    };

    Estandar = () => {
        $State.go(this.props, 'estandar', {});
    };

    render() {

        let Usr = ReactLocalStorageService.get('Usr') || {};

        const { classes } = this.props;

        return (
            <Fragment>
                <AppBar style={{background: '#424242'}} position="static" className={classes.appBar}>
                    <Toolbar>
                        <IconButton edge="start" color="inherit" aria-label="menu" onClick={this.openMenu}>
                            <MenuOutlined/>
                        </IconButton>
                        <Typography variant="h6" style={{flexGrow: 1}}>
                            Charlie Brown
                        </Typography>
                        <Button color="inherit" onClick={this.Login}>Login</Button>
                    </Toolbar>
                </AppBar>
                <nav className={classes.drawer} aria-label="mailbox folders">
                    {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
                    <Hidden smUp implementation="css">
                        <Drawer
                            container={container}
                            variant="temporary"
                            anchor={theme.direction === 'rtl' ? 'right' : 'left'}
                            open={mobileOpen}
                            onClose={handleDrawerToggle}
                            classes={{
                                paper: classes.drawerPaper,
                            }}
                            ModalProps={{
                                keepMounted: true, // Better open performance on mobile.
                            }}
                        >
                            {drawer}
                        </Drawer>
                    </Hidden>
                    <Hidden xsDown implementation="css">
                        <Drawer
                            classes={{
                                paper: classes.drawerPaper,
                            }}
                            variant="permanent"
                            open
                        >
                            {drawer}
                        </Drawer>
                    </Hidden>
                </nav>
                {/*<Drawer open={this.state.menu} onClose={this.closeMenu}>

                    <div style={{
                        height: '100px',
                        width: '300px',
                        textAlign: 'center',
                        padding: '20px',
                        background: '#424242',
                        color: 'white'
                    }}>
                        <div style={{
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            height: '100%',
                            fontSize: '20px',
                            textTransform: 'capitalize'
                        }}>
                            {Usr.username}
                        </div>
                    </div>

                    <List>
                        <ListItem button onClick={this.Home}>
                            <ListItemIcon>
                                <PersonOutlined/>
                            </ListItemIcon>
                            <ListItemText primary={'Usuario'}/>
                        </ListItem>

                        {Usr.id_cat_tipo_permiso == 1 ? <ListItem button onClick={this.Administrador}>
                                <ListItemIcon>
                                    <ListAltOutlined/>
                                </ListItemIcon>
                                <ListItemText primary={'Rol Administrador'}/>
                            </ListItem> :
                            <ListItem button onClick={this.Estandar}>
                                <ListItemIcon>
                                    <ListAltOutlined/>
                                </ListItemIcon>
                                <ListItemText primary={'Rol Estandar'}/>
                            </ListItem>
                        }
                    </List>
                </Drawer>*/}
            </Fragment>
        );
    }
}

export default withStyles(styles)(Header);
