import React, {Component, Fragment} from "react";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import {HomeOutlined, ListAltOutlined, MenuOutlined, PersonOutlined} from "@material-ui/icons";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import $State, {ReactLocalStorageService} from '../settings/Libs/Libs';
import Drawer from "@material-ui/core/Drawer";
import Hidden from '@material-ui/core/Hidden';
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import {makeStyles, useTheme} from '@material-ui/core/styles';
import Divider from '@material-ui/core/Divider';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import PropTypes from 'prop-types';


const drawerWidth = 240;

const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
    },
    drawer: {
        [theme.breakpoints.up('sm')]: {
            width: drawerWidth,
            flexShrink: 0,
        },
    },
    appBar: {
        [theme.breakpoints.up('sm')]: {
            width: `calc(100% - ${drawerWidth}px)`,
            marginLeft: drawerWidth,
        },
    },
    menuButton: {
        marginRight: theme.spacing(2),
        [theme.breakpoints.up('sm')]: {
            display: 'none',
        },
    },
    toolbar: theme.mixins.toolbar,
    drawerPaper: {
        width: drawerWidth,
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },
}));

function ResponsiveDrawer(props) {
    this.state.props = props;
    this.state.classes = useStyles();
    this.state.theme = useTheme();
    this.state.mobileOpen = React.useState(false);
    this.state.setMobileOpen = React.useState(false);

    this.state.handleDrawerToggle = () => {
        this.state.setMobileOpen(!this.state.mobileOpen);
    };

    this.state.drawer = (
        <div>
            <div className={classes.toolbar}/>
            <Divider/>
            <List>
                {['Inbox', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
                    <ListItem button key={text}>
                        <ListItemIcon>{index % 2 === 0 ? <InboxIcon/> : <MailIcon/>}</ListItemIcon>
                        <ListItemText primary={text}/>
                    </ListItem>
                ))}
            </List>
            <Divider/>
            <List>
                {['All mail', 'Trash', 'Spam'].map((text, index) => (
                    <ListItem button key={text}>
                        <ListItemIcon>{index % 2 === 0 ? <InboxIcon/> : <MailIcon/>}</ListItemIcon>
                        <ListItemText primary={text}/>
                    </ListItem>
                ))}
            </List>
        </div>
    );
}

class Header extends Component {

    state = {};

    constructor(props) {
        super(props);
        this.state = {
            menu: false,
            props: '',
            classes: '',
            theme: '',
            mobileOpen: '',
            setMobileOpen: '',
            handleDrawerToggle: '',
            drawer: ''

        };
    }

    openMenu = () => {
        this.setState({
            menu: true
        })
    };

    closeMenu = () => {
        this.setState({
            menu: false
        })
    };

    Login = () => {
        $State.go(this.props, 'login', {});
    };

    Home = () => {
        $State.go(this.props, 'home', {});
    };

    Administrador = () => {
        $State.go(this.props, 'administrador', {});
    };

    Estandar = () => {
        $State.go(this.props, 'estandar', {});
    };

    render() {

        let Usr = ReactLocalStorageService.get('Usr') || {};

        const classes = this.state.classes;
        const setMobileOpen = this.state.setMobileOpen;

        return (
            <Fragment>
                <AppBar style={{background: '#424242'}} position="static" className={classes.appBar}>
                    <Toolbar>
                        <IconButton edge="start" color="inherit" aria-label="menu" onClick={this.openMenu}>
                            <MenuOutlined/>
                        </IconButton>
                        <Typography variant="h6" style={{flexGrow: 1}}>
                            Charlie Brown
                        </Typography>
                        <Button color="inherit" onClick={this.Login}>Login</Button>
                    </Toolbar>
                </AppBar>
                <nav className={classes.drawer} aria-label="mailbox folders">
                    {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
                    <Hidden smUp implementation="css">
                        <Drawer
                            container={container}
                            variant="temporary"
                            anchor={theme.direction === 'rtl' ? 'right' : 'left'}
                            open={mobileOpen}
                            onClose={handleDrawerToggle}
                            classes={{
                                paper: classes.drawerPaper,
                            }}
                            ModalProps={{
                                keepMounted: true, // Better open performance on mobile.
                            }}
                        >
                            {drawer}
                        </Drawer>
                    </Hidden>
                    <Hidden xsDown implementation="css">
                        <Drawer
                            classes={{
                                paper: classes.drawerPaper,
                            }}
                            variant="permanent"
                            open
                        >
                            {drawer}
                        </Drawer>
                    </Hidden>
                </nav>
                {/*<Drawer open={this.state.menu} onClose={this.closeMenu}>

                    <div style={{
                        height: '100px',
                        width: '300px',
                        textAlign: 'center',
                        padding: '20px',
                        background: '#424242',
                        color: 'white'
                    }}>
                        <div style={{
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            height: '100%',
                            fontSize: '20px',
                            textTransform: 'capitalize'
                        }}>
                            {Usr.username}
                        </div>
                    </div>

                    <List>
                        <ListItem button onClick={this.Home}>
                            <ListItemIcon>
                                <PersonOutlined/>
                            </ListItemIcon>
                            <ListItemText primary={'Usuario'}/>
                        </ListItem>

                        {Usr.id_cat_tipo_permiso == 1 ? <ListItem button onClick={this.Administrador}>
                                <ListItemIcon>
                                    <ListAltOutlined/>
                                </ListItemIcon>
                                <ListItemText primary={'Rol Administrador'}/>
                            </ListItem> :
                            <ListItem button onClick={this.Estandar}>
                                <ListItemIcon>
                                    <ListAltOutlined/>
                                </ListItemIcon>
                                <ListItemText primary={'Rol Estandar'}/>
                            </ListItem>
                        }
                    </List>
                </Drawer>*/}
            </Fragment>
        );
    }
}

ResponsiveDrawer.propTypes = {
    container: PropTypes.instanceOf(typeof Element === 'undefined' ? Object : Element),
};

export default Header;
