import React, {Component, Fragment} from 'react';
import SexoService from "../../services/Sexo/SexoService";
import Header from "../../includes/Header";
import Grid from '@material-ui/core/Grid';
import {AddOutlined, DeleteOutlined, EditOutlined, SearchOutlined} from '@material-ui/icons';
import BotonFlotante from "../../includes/BotonFlotante";
import ModalSexo from "./includes/ModalAdministrador";
import Button from "../Login/includes/ModalRegistro";

class Administrador extends Component {

    state = {};

    constructor(props) {
        super(props);
        this.state = {
            params: props.match.params,

            lista: [],
        };
        this.all();
    }

    RefrechList = () => {
        this.all();
    };

    all = () => {
        SexoService.all().then(response => {
            this.setState({
                lista: response.data
            });
        }).catch(error => {
            this.setState({
                lista: []
            });
            alert(error.mensaje);
        });
    };

    delete = (item) => {
        SexoService.delete(item).then(response => {
            alert(response.mensaje);
            this.RefrechList();
        }).catch(error => {
            alert(error.mensaje);
        });
    };

    render() {

        const {params} = this.props.match;

        return (
            <Fragment>

                <Header {...this.props}/>

                <Grid item xs={6} sm={6} md={6} lg={4} xl={4}>
                    <Button variant="contained" onClick={() => this.close()}>
                        Cerrar
                    </Button>
                </Grid>


                <ModalSexo
                    tipo={'add'}
                    item={{}}
                    RefrechList={this.RefrechList}
                    componente={<BotonFlotante icono={<AddOutlined/>}/>}
                />

            </Fragment>
        );
    }
}

export default Administrador;
