import React, {Component} from 'react';
import PropTypes from 'prop-types';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import UsuarioService from "../../../services/Usuario/UsuarioService";
import CargoService from "../../../services/Cargo/CargoService";
import MesesService from "../../../services/Meses/MesesService";
import moment from "moment";


class ModalCargo extends Component {

    state = {};

    constructor(props) {
        super(props);
        this.state = {
            open: false,

            monto_cargo: 750.00,
            id_usuario: '',
            id_cat_mes: '',
            mes: '',

            cat_meses: []
        };
    }

    open = () => {
        let item = this.props.item;
        this.setState({
            open: true,
            id_cat_mes: item.id_cat_mes || '',
            mes: moment(item.mes).format("DD MM/YYYY") || '',
        });
        if (item.id_cat_sexo > 0) {
            this.show(item);
        }
    };

    close = () => {
        this.setState({
            open: false,

            id_cat_mes: '',

        });
    };


    show = (item) => {
        UsuarioService.show(item).then(response => {
            this.setState({
                id_usuario: response.data.id_usuario || '',
                id_cat_sexo: response.data.id_cat_sexo || '',
            });
        }).catch(error => {
            alert(error.mensaje);
        });
    };

    save = () => {
        this.cargo();
        // if (this.state.id_usuario > 0) {
        //     this.update();
        // } else {
        //     this.create();
        // }
    };

    cargo = () => {
        CargoService.all(this.state).then(response => {
            alert(response.mensaje);
            this.close();
        }).catch(error => {
            alert(error.mensaje);
        });
    };

    update = () => {
        UsuarioService.update(this.state).then(response => {
            alert(response.mensaje);
            this.props.RefrechList();
            this.close();
        }).catch(error => {
            alert(error.mensaje);
        });
    };



    render() {
        return (
            <div>

				<span onClick={this.open}>
					{this.props.componente}
				</span>

                <Dialog
                    open={this.state.open}
                    onClose={() => this.close()}
                    aria-labelledby="alert-dialog-title"
                    aria-describedby="alert-dialog-description"
                    maxWidth="sm"
                >

                    <DialogTitle>
                        Cargo
                    </DialogTitle>

                    <DialogContent>
                        <Grid container spacing={2}>

                            <Grid item xs={12} sm={12} md={12} lg={12} xl={12}>
                                <label>Seguro que desea realizar el cargo en la fecha { this.state.mes} por la cantidad de ${this.state.monto_cargo}</label>
                            </Grid>

                            <Grid item xs={6} sm={6} md={6} lg={4} xl={4}>
                                <TextField
                                    label="Cargo"
                                    type="text"
                                    margin="normal"
                                    variant="outlined"
                                    fullWidth
                                    value={this.state.monto_cargo}
                                    onChange={(e) => {
                                        this.setState({
                                            monto_cargo: e.target.value
                                        });
                                    }}
                                    disabled={this.props.tipo === 'view'}
                                />
                            </Grid>

                        </Grid>
                    </DialogContent>

                    <DialogActions>
                        <Button variant="contained" onClick={() => this.close()}>
                            Cancelar
                        </Button>
                        <Button variant="contained" color="primary" onClick={() => this.save()} autoFocus>
                            Aceptar
                        </Button>
                    </DialogActions>

                </Dialog>
            </div>
        );
    };

}

ModalCargo.propTypes = {
    id: PropTypes.oneOfType([
        PropTypes.number.isRequired,
        PropTypes.oneOf([null]).isRequired,
    ]),
    tipo: PropTypes.string.isRequired,
    item: PropTypes.object.isRequired,
    componente: PropTypes.element.isRequired
};

export default ModalCargo;
