import {HttpRequest} from '../../settings/Libs/Libs';

class SaldoService {

    static all = (item) => {
        let params = {
            "monto_cargo": item.monto_cargo || 0,
            "fecha_cargo": item.fecha_cargo || ''
        };
        return new Promise((resolve, reject) => {
            HttpRequest.post('_cargo_agregar', params).then((response) => {
                resolve(response);
            }).catch((error) => {
                reject(error);
            });
        });
    };

    static add = (item) => {
        let params = {
            id_usuario: item.id_usuario,
            monto_saldo: item.monto_saldo,
        };
        return new Promise((resolve, reject) => {
            HttpRequest.post('_saldo_add', params).then((response) => {
                resolve(response);
            }).catch((error) => {
                reject(error);
            });
        });
    };
}

export default SaldoService;
