    state = {};

    constructor(props) {
        super(props);
        this.state = {
            params: props.match.params,
        };
    }


    RefrechList = () => {
        this.all();
    };

    all = () => {
        UsuarioService.allEstandar().then(response => {
            this.setState({
                lista: response.data
            });
        }).catch(error => {
            this.setState({
                lista: []
            });
            alert(error.mensaje);
        });
    };

    meses = () => {
        MesesService.get().then(response => {
            this.setState({
                cat_meses: response.data
            });
        }).catch(error => {
            this.setState({
                cat_meses: []
            });
            alert(error.mensaje);
        });
    };

    delete = (item) => {
        SexoService.delete(item).then(response => {
            alert(response.mensaje);
            this.RefrechList();
        }).catch(error => {
            alert(error.mensaje);
        });
    };

    check_mes = (item) => {
        var mes = "";
        for (var x = 0; x < this.state.cat_meses.length; x++) {
            if (this.state.cat_meses[x].id == item) {
                mes = this.state.cat_meses[x].mes
            }
        }

        this.setState({
            mes: mes
        })
        return mes;
    }


